   124  "java": {
   125    "include": ["**/*.java", "**/*.xml", "**/*.properties", "pom.xml", "build.gradle"],
   126    "exclude": ["target/**", "build/**", ".gradle/**"]
   127  },
   128  "go": {
   129    "include": ["**/*.go", "go.mod", "go.sum", "**/*.yaml"],
   130    "exclude": ["vendor/**", "bin/**"]
   131  },
   132  "generic": {
   133    "include": ["**/*.md", "**/*.txt", "Dockerfile*", "*.env*"],
   134    "exclude": ["*.log", ".git/**", "tmp/**", "temp/**"]
